    private void Rb_REDActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Rb_REDActionPerformed
        Txt_Harga.setText("4800000");
        if(Rb_RED.isSelected())
        {
          Rb_YELLOW.setSelected(false);
          Rb_GREEN.setSelected(false);
          Rb_PINK.setSelected(false);
          Rb_PURPLE.setSelected(false);
        }
    }//GEN-LAST:event_Rb_REDActionPerformed

