    private void Txt_jlhPesananKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_Txt_jlhPesananKeyReleased
        int Harga = Integer.valueOf(Txt_Harga.getText());
        
        String output;
        double a = Double.valueOf(Txt_jlhPesanan.getText());
        double b = Double.valueOf(Txt_Harga.getText());
        double hasil = Harga * a;
        output = String.valueOf(hasil);
        Txt_total.setText(output);
    }//GEN-LAST:event_Txt_jlhPesananKeyReleased

    private void B_PesanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_B_PesanActionPerformed
        jTextArea1.setText(
                   "Nama                             : "+ Txt_nama.getText()+ "\n"+
                   "E-Mail                             : "+ Txt_email.getText()+ "\n"+
                   "Harga Tiket                    : "+ Txt_Harga.getText()+ "\n"+
                   "Jumlah Pesanan          : "+ Txt_jlhPesanan.getText()+ "\n"+
                   "Total                                : "+ Txt_total.getText()+ "\n");
       
    }//GEN-LAST:event_B_PesanActionPerformed

