    private void B_KeluarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_B_KeluarActionPerformed
        int jawab = JOptionPane.showConfirmDialog(this,"Anda yakin ingin keluar","Keluar", 
                JOptionPane.YES_NO_OPTION, 
                JOptionPane.QUESTION_MESSAGE);
        if(jawab==0){
          JOptionPane.showMessageDialog(this,"Anda akan keluar dari program ini");
          System.exit(0);
        }
    }//GEN-LAST:event_B_KeluarActionPerformed

